INSERT INTO actions (category, content, author, rank) VALUES ('Giving back', 'Donate to an organisation that supports education', 'Inclusiv.me', 2);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'Bring in third-party experts for trainings on how to create a welcoming and inclusive environment', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Support', 'if you witness someone being bullied, speak up on their behalf if you feel safe doing so', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Support', 'if you witness someone being bullied, pull them out of that situation by offering them to go somewhere else together', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Support', 'if you witness someone being bullied, talk to them and change the topic of the conversation', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'offer trainings on transgender sensitivities', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'Share peer support groups for minorities, for example for queer women in leadership positions', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('I’m the minority', 'Create a peer support meetup group where people like you can support each other and share experiences', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('I’m the minority', 'Offer an adoption leave policy whatever the age of the adopted child', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Social', 'When someone expresses an opinion different from your own listen carefuly and try to understand them without voicing your own opinion for at least 10 minutes', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'When someone expresses an idea show them shortcommings by discussing the idea rather than by asking trick questions', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'Some people will be stimulated by challenging trick questions that point at their shortcomings. Others will feel demotivated and are more productive when provided constructive feedback. Try to figure out how a person best recieves feedback and adapt your comments to them. ', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'Be upfront with potential employees about the company policies in place and the values you uphold to make sure everyone you hire will uphold these values', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'let candidates know what resources you offer to promote inclusion and diversity in your company. It will show minorities you are commited and will help hiring candidates that share this commitment', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Work', 'Organise a pride party in your office to celebrate how incluse all your employees are', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 5 organise an event to celebrate the Father of the Khalsa Guru Gobind Singh Ji’s birthday: Tenth Guru of the Sikhs who initiated the Sikhs as the Khalsa (the pure ones) ', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 6 organise an event to celebrate Epiphany: a holiday recognizing the visit of the three wise men to the baby Jesus 12 days after his birth', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 7: organise an event to celebrate Christmas for Eastern Orthodox Christians, who follow the Julian calendar rather than the Gregorian one', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Langage', 'Say _happy holidays_ rather than merry christmas (or other specific holidays) to include people who may not celebrate the same holidays as you', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 14: organise an event to celebrate _Makar Sankranti_, a major indian harvest festiva', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'At sundown on January 17 organise an event to celebrate World Religion Day, observed by those of the Baha’i faith to promote interfaith harmony and understanding', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 18: organise an event to celevrate Martin Luther King Day, commemorating the birth of the recipient of 1964 Nobel Peace Prize and an activist for non-violent social change until his assassination in 1968', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On the first full moon of January: celebrate the Mahayana Buddhist New Year', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 25: celebrate TuB’shvat by eating dried fruits and nuts, planting trees or decorating with almond flowers, on the the fifteenth day of the Hebrew month of Shevat,  this Jewish holiday recognizes _The New Year of the Trees_', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 26: celebrate the Republic Day of India, recognizing Constitution of India came into law in 1950, replacing the Government of India Act of 1935 and India’s 1930 declaration of independence', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 27: commemorate UN Holocaust Memorial Day, the Annual International Day of Commemoration to remember the victims of the Holocaust and the liberation of the Auschwitz death camp in 1945', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On January 31: celebrate the birthday of Guru Har Rai, the seventh Sikh guru.', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On February 1: organise an event for National Freedom Day celebrates the signing of the 13th Amendment, which abolished slavery in 1865', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On February 9, share a pancake brunch for Mardi Gras, also known as Fat Tuesday or Shrove Tuesday, the last day of feasting before Lent begins on Ash Wednesday', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On February 10, organise a charity event for Ash Wednesday, the beginning of Lent in the Christian faith. During the seven weeks prior to Easter, the Christian lent is a time of reflection and preparation for the Holy Week and is observed by fasting, charitable giving, and worshipping', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'Between February 8 and 22: Organise an event for the lunar calendar New Year celebrated by the Chinese, Korean, and Vietnamese on the 8th and on Losar the 9th by the Tibetans. Chinese New Year is recognized by gift giving, parades, decorations, and feasting. The celebration culminates with the Lantern Festival on February 22', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On February 15, celebrate president day AND Nirvana Day, the commemoration of Buddha’s death at the age of 80, when he reached the zenith of Nirvana. February 8 is an alternative date of observance', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'February 26-29: Intercalary Days observed by the Baha’i faith through gifs and charitable acts in preparation for the fasting before their new year. Intercalary days are are added to the Baha’i calendar to maintain their solar calendar', 'Inclusiv.me', 1);
INSERT INTO actions (category, content, author, rank) VALUES ('Celebrations', 'On March 8, International Women’s Day honoring women’s economic, political, and social achievements and include the Hindu holiday Maha Shivaratri which celebrates the godess Shiva', 'Inclusiv.me', 1);